var timing=global.get("timing");
//if ( typeof global.get("days") != 'undefined' ) context.days=global.get("days");

switch (msg.payload)
    {
    case "u" :  if ((context.selector>4)&&(context.selector<29))
                {
                    timing[((context.days-1)*24)+context.selector-5]+=0.5; 
                    if (timing[((context.days-1)*24)+context.selector-5]>24){ timing[((context.days-1)*24)+context.selector-5]=24; msg.foryou="Limite raggiunto";}
                    else msg.foryou="Temperatura aumentata";
                }
                if (context.selector==1) { if (timing[168+(context.selector-1)]<24) {  timing[168+(context.selector-1)]+=0.5; msg.foryou="Temperatura clima aumentata di 0.5°C"; } else msg.foryou="Limite raggiunto"; }
                if (context.selector==2) { if (timing[168+(context.selector-1)]<24) {  timing[168+(context.selector-1)]+=0.5; msg.foryou="Temperatura fuori casa aumentata di 0.5°C"; } else msg.foryou="Limite raggiunto"; }
                if (context.selector==29) { msg.foryou="Giorno successivo"; context.days++; if (context.days>7) { context.days = 1; } }
                break;
    case "d" :  if ((context.selector>4)&&(context.selector<29))
                {
                    timing[((context.days-1)*24)+context.selector-5]-=0.5; 
                    if (timing[((context.days-1)*24)+context.selector-5]<14){ timing[((context.days-1)*24)+context.selector-5]=14; msg.foryou="Limite raggiunto";}
                    else msg.foryou="Temperatura decrementata";
                }
                if (context.selector==1) { if (timing[168+(context.selector-1)]>12) { timing[168+(context.selector-1)]-=0.5; msg.foryou="Temperatura clima diminuita di 0.5°C"; } else msg.foryou="Limite raggiunto"; }
                if (context.selector==2) { if (timing[168+(context.selector-1)]>12) { timing[168+(context.selector-1)]-=0.5; msg.foryou="Temperatura fuori casa diminuita di 0.5°C"; } else msg.foryou="Limite raggiunto"; }
                if (context.selector==29) {  msg.foryou="Giorno precedente";  context.days--; if (context.days < 1) { context.days = 7;  } }
                break;
    case 'r' :  if ((context.selector>=5)&&(context.selector<28))
                        {
                         timing[((context.days-1)*24)+context.selector-4]=timing[((context.days-1)*24)+context.selector-5];   
                         context.selector++;
                         msg.foryou="Temperatura copiata nello slot successivo";
                        }
                if ((context.selector==29)&&(context.days<7))
                        {
                         for (var a=0;a<24;a++)
                            {
                             timing[((context.days)*24)+a]=timing[((context.days-1)*24)+a];   
                            }
                         context.days++;
                         msg.foryou="Impostazioni copiate nel giorno successivo";
                        }
                        else if(context.days==7)
                            msg.foryou="Fine della settimana raggiunto!";
                break;
    case 's': context.saving=0;  msg.foryou="Impostazioni salvate"; break;
    case '1':
    case '2':
    case '3':
    case '4':
    case '5':
    case '6':
    case '7':
    case '8':
    case '9':
    case '10':
    case '11':
    case '12':
    case '13':
    case '14':
    case '15':
    case '16':
    case '17':
    case '18':
    case '19':
    case '20':
    case '21':
    case '22':
    case '23':
    case '24':
    case '25':
    case '26':
    case '27':
    case '28':
    case '29': context.selector=parseInt(msg.payload);
               if (msg.payload=='1') msg.foryou="Temperatura clima selezionata"; 
               else if (msg.payload=='2') msg.foryou="Temperatura fuori casa selezionata";
               else if (msg.payload=='29') msg.foryou="Giorno selezionato";
               else  msg.foryou="Regolazione temperatura ore: " + (parseInt(msg.payload)-5);
    break;
    case 'c' : msg.payload="niente";  msg.foryou="Cambiamenti annullati"; node.send([null,null,msg]);
    }

msg.temperatures=context.temperatures;
msg.timing=timing;
