if (flow.get("thermostat_mode") != "schedule") {
    return null;
}

var timing=global.get("timing");
var now = new Date(msg.time);

var fixed_temperature = timing[(now.getDay()*24)+now.getHours()];
var away_temperature = timing[169];
var away_mode = flow.get("away_mode");

if (away_mode) {
    msg.payload = (fixed_temperature > away_temperature ? away_temperature : fixed_temperature);
    msg.awayMode = true;
} else {
    msg.payload = fixed_temperature;
    msg.awayMode = false;
}

node.status({fill:"blue",shape:"dot",text: "Actual: " + flow.get("ambient_temperature") + " - Set point: " + msg.payload + "°C" + " [away mode: " + away_mode + "]"});


return msg;

context.global.timing=JSON.parse(msg.payload);
msg.payload="";
return msg;

msg.topic = 'awayMode';
var awayMode = false;

if(msg.payload == "armed_away"){
    awayMode = true; 
}

flow.set("away_mode",awayMode);
node.status({fill:"red",shape:"dot",text:"away mode: " + awayMode});  

return msg;

msg.topic = "update";

var msg2 = {};
msg2.payload = flow.get("thermostat_mode");

var data = {
    'ambient_temperature': flow.get("ambient_temperature") || 20,
    'target_temperature': flow.get("target_temperature") || 14,
    'hvac_state': flow.get("hvac_action") || 'off',
    'has_leaf': flow.get("away_mode") || false,
    'off_view': flow.get("off_view") || false
}
msg.payload = data;
node.status({fill:"red",shape:"dot",text: "ambient_temperature: " + data.ambient_temperature + " / target_temperature: " + data.target_temperature + " / away_mode: " + data.has_leaf + " / climate_state: " + flow.get("climate_state") + " / hvac_state: " + data.hvac_state + " / thermostat_mode: " + msg2.payload});

var msg3 = {};
if (msg2.payload == "off")
{
    msg3.payload = false;
}
else if (msg2.payload == "countdown")
{
    msg3.payload = true;
}
else
{
    msg3 = null;
}

return [msg,msg2,msg3];

if (msg.topic == "target_temperature") {
    msg.payload = msg.payload.target_temperature;
    flow.set("target_temperature", msg.payload);
    node.status({fill:"red",shape:"dot",text:"target_temperature: " + msg.payload}); 
    return msg;
}

msg.topic = 'climate';

flow.set("ambient_temperature", msg.data.new_state.attributes.current_temperature);
flow.set("target_temperature",msg.data.new_state.attributes.temperature);
