var msg2 = {};

if (msg.payload == "off"){
    msg.payload = false;
    flow.set("thermostat_mode","off");
    msg2.payload = "off";
}
else {
    msg.payload = true;
    if (flow.get("thermostat_mode") == "off") {
        flow.set("thermostat_mode","schedule");
        msg2.payload = "schedule";
    }
    else {
        msg2.payload = flow.get("thermostat_mode");
    }
}

return [msg, msg2];

