return [msg1, msg2];

var msg2 = {};
msg2.payload = msg.payload;

var msg3 = {};

var minutes = msg.payload /60;
msg.payload=minutes;

if (msg.state > 0)
{
    if (flow.get("thermostat_mode") != 'countdown')
    {
        flow.set("thermostat_mode", "countdown");
    }
    else
    {
        msg2 = null;
    }

    if (minutes < 1)
    {
        msg3.payload = "Secondi rimanenti: " + (minutes*60).toFixed(0);
    }
    else
    {
        msg3.payload = "Minuti rimanenti: " + minutes.toFixed(0);
    }
}
else if (msg2.payload == -1 || (msg2.payload == 0 && flow.get("countdown_preflag") != 'off'))
{
    flow.set("thermostat_mode", flow.get("countdown_followed_by"));
}
else
{
    msg2 = null;
}

flow.set("countdown_preflag", msg.flag);

return [msg, msg2, msg3];

var seconds = msg.payload * 60;

if(seconds==0) 
{
    msg.payload= "stop";
    return msg;
}

msg.payload="{\"payload\": \"on\", \"timeout\":" + seconds + ", \"warning\": 30}"

return msg;

var minuti = msg.payload;

if(minuti==0) 
{
    minuti=1;
}

msg.ui_control = {"min":0,"max":minuti}

return msg;

switch (msg.payload){
case 'off':
    flow.set("thermostat_mode","off")
    return [msg,null,null,null]

case 'schedule':
    msg.payload = {"group":{"show":["Cronotermostato_Programmazione"],"hide":["Cronotermostato_Nest"]}};
    flow.set("thermostat_mode","schedule")
    return [null,msg,null,null]

case 'countdown':
    msg.payload = {"group":{"show":["Cronotermostato_Countdown"],"hide":["Cronotermostato_Nest"]}};
    return [null,null,msg,null]

case 'manual':
    flow.set("thermostat_mode","manual")
    return [null,null,null,msg]
}

return [null,null,null,null];

