    flow.set("countdown_followed_by","off")
    return msg;

case 'schedule':
    flow.set("countdown_followed_by","schedule")
    return msg;
}

return msg;

var button_link = {};
button_link.group = {};

var msg2 = {};

switch (msg.payload){
case 'off':
    msg.ui_control = {"icon": ""};
    msg.label = "";
    msg.enabled = false;
    button_link.group.show = [];
    button_link.group.hide = [];
    msg2.payload = "";
    break;
case 'schedule':
    msg.ui_control = {"icon": "fa-calendar"};
    msg.label = "Imposta programma";
    msg.enabled = true;
    button_link.group.show = ["Cronotermostato_Programmazione"];
    button_link.group.hide = ["Cronotermostato_Nest"];
    msg2.payload = "";
    break;
case 'countdown':
    msg.ui_control = {"icon": "timer"};
    msg.label = "Imposta Countdown";
    msg.enabled = true;    
    button_link.group.show = ["Cronotermostato_Countdown"];
    button_link.group.hide = ["Cronotermostato_Nest"];
    msg2 = null;
    break;
case 'manual':
    msg.ui_control = {"icon": ""};
    msg.label = "";
    msg.enabled = false;
    button_link.group.show = [];
    button_link.group.hide = [];
    msg2.payload = "(Ricordarsi di spegnere il termostato)";
    break;
}

flow.set("setting_link", button_link);
return [msg, msg2];

